import React, { useState } from "react";
import './styles/App.css'
import PostList from "./components/PostList.jsx";
import MyButton from "./components/UI/button/MyButton.jsx"
import MyInput from "./components/UI/input/MyInput.jsx";

function App() {
  const [posts, setPost] = useState([
    {id: 1, title: 'Javascript 1', body: 'Description'},
    {id: 2, title: 'Javascript 2', body: 'Description'},
    {id: 3, title: 'Javascript 3', body: 'Description'}
  ])

  const [title, setTitle] = useState('')
  const [body, setBody] = useState('')
  const addNewPost = () => {
      const newPost = {
        id: Date.now(),
        title,
        body
      }
      setPost([...posts, newPost])
      setTitle('')
  }

  return (
    <div className="App">
      <form>
        <MyInput value={title} type="text" placeholder="Name of post"  onChange={(e) => setTitle(e.target.value) }/>
        <MyInput value={body} type="text" placeholder="Description of post" onChange={(e) => setBody(e.target.value)}/>
        <MyButton type='button' onClick={addNewPost}>Create new post</MyButton>
      </form>
      <PostList posts={posts} title={'Posts about JS'}/>
    </div>
  );
}

export default App;
