import React, { useEffect, useState } from "react";
import './styles/App.css'
import PostList from "./components/PostList.jsx";
import PostForm from "./components/PostForm.jsx";
import PostFilter from "./components/PostFilter.jsx";
import MyModal from "./components/UI/MyModal/MyModal.jsx";
import MyButton from "./components/UI/button/MyButton.jsx";
import { usePost } from "./hooks/usePosts.js";
import PostService from "./API/PostService.js";
import Loader from "./components/UI/loader/Loader.jsx";
import { useFetching } from "./hooks/useFetching.js";
import { getPagesCount } from "./utils/pages.js";
import { usePagination } from "./hooks/usePagination.js";

function App() {
  const [posts, setPosts] = useState([])
  const [filter, setFilter] = useState({sort: '', query: ''})
  const [modal, setModal] = useState(false)
  const [totalPages, setTotalPages] = useState(0)
  const [limit, setLimit] = useState(10)
  const [page, setPage] = useState(1)
  const pagesArray = usePagination(totalPages)
  const sortedAndSearchedPosts = usePost(posts, filter.sort, filter.query)

  const [fetchPosts, isPostsLoading, postError] = useFetching(async() => {
    const responce = await PostService.getAll(limit, page)
    setPosts(responce.data)
    const totalCount = responce.headers['x-total-count']
    setTotalPages(getPagesCount(totalCount, limit))
  })

  useEffect(() => {
    fetchPosts()
  }, [])

  const createPost = (newPost) => {
    setPosts([...posts, newPost])
    setModal(false)
  }

  const removePost = (post) => {
    setPosts(posts.filter(p => p.id !== post.id))
  }

  return (
    <div className="App">
      <MyModal visible={modal} setVisible={setModal}>
        <PostForm create={createPost}/>
      </MyModal>

      <MyButton onClick={() => setModal(true)} style={{marginTop: 30}}>Create new Post</MyButton>

      <hr style={{margin: "15px 0"}}/>

      <PostFilter 
        filter={filter} 
        setFilter={setFilter}
      />
        {postError && 
          <h1>An error {postError} has ocured</h1>
        }
      {isPostsLoading
        ? <div style={{display: 'flex', justifyContent: 'center', marginTop: 50}}><Loader /></div>
        : <PostList remove={removePost} posts={sortedAndSearchedPosts} title={'Posts about JS'}/>
      }
      <div className="pages__btns">
        {pagesArray.map(el => {
        return <div className={page === el ? 'page__btn' : 'page' 

        } key={el}>
          <MyButton>{el}</MyButton>
        </div> 
      })}
      </div>
    </div>
  );
}

export default App;
